import React from 'react';
import ReactCardFlip from 'react-card-flip';
import Valyrian from '../assets/Valyrian.png';

class FlipCard extends React.Component {
  constructor() {
    super();
    this.state = {
      isFlipped: false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    e.preventDefault();
    this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
  }

  render() {
    return (
      <div
        className="relative min-h-screen bg-fixed bg-cover bg-center"
        style={{ backgroundImage: `url(${Valyrian})` }}
      >
        <div className="bg-black bg-opacity-40 rounded-lg p-4 sm:p-6 md:p-8 shadow-lg w-full max-w-3xl sm:max-w-4xl md:max-w-5xl lg:max-w-6xl mx-auto">
          <h2 className="text-3xl font-bold text-white mb-6 text-center tracking-wider font-cinzeldecorative">
            Gamezz
          </h2>

          <ReactCardFlip isFlipped={this.state.isFlipped} flipDirection="vertical">
            {/* Front of the card */}
            <div className="front-card">
              <h3 className="text-2xl text-white text-center">This is the front of the card.</h3>
              <button
                className="mt-4 bg-blue-500 text-white py-2 px-4 rounded"
                onClick={this.handleClick}
              >
                Click to flip
              </button>
            </div>

            {/* Back of the card */}
            <div className="back-card">
              <h3 className="text-2xl text-white text-center">This is the back of the card.</h3>
              <button
                className="mt-4 bg-red-500 text-white py-2 px-4 rounded"
                onClick={this.handleClick}
              >
                Click to flip back
              </button>
            </div>
          </ReactCardFlip>
        </div>
      </div>
    );
  }
}

export default FlipCard;
