import React, { useEffect, useRef } from 'react';

const FireAnimation = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    const config = {
      sparkFreq: 1,
      meanSparkSize: 0.012,
      meanSparkLife: 200,
      meanSparkVelocity: [2, 6],
      sparkSizeVariation: 5,
      sparkBlink: 10, // Lower is more blink
      floorHeight: 0.15
    };

    const resize = () => {
      canvas.height = document.body.offsetHeight;
      canvas.width = document.body.offsetWidth;
    };

    resize();
    window.addEventListener('resize', resize);

    config.meanSparkSize = canvas.width * config.meanSparkSize;

    const fire = new Fire(ctx, canvas, canvas.height - canvas.height * config.floorHeight, config);

    const loop = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      fire.update();
      for (let i = 0; i < config.sparkFreq; i++) {
        fire.spark(Math.random() * canvas.width);
      }
      requestAnimationFrame(loop);
    };

    const requestAnimFrame = () => {
      return (
        window.requestAnimationFrame ||
        window.webkitRequestAnimationFrame ||
        window.mozRequestAnimationFrame ||
        window.oRequestAnimationFrame ||
        window.msRequestAnimationFrame ||
        (a => window.setTimeout(a, 1000 / 60))
      );
    };

    loop();

    // Clean up event listener on component unmount
    return () => {
      window.removeEventListener('resize', resize);
    };
  }, []);

  return (
    <div className="padded" style={{ background: '#111', padding: '0 10%' }}>
      <canvas ref={canvasRef} style={{ width: '100%', height: '100%', maxHeight: '400px' }} />
    </div>
  );
};

class Fire {
  constructor(ctx, canvas, y, config) {
    this.ctx = ctx;
    this.canvas = canvas;
    this.y = y;
    this.r = 255;
    this.config = config;
    this.sparks = [];
  }

  spark(x) {
    this.sparks.push(new Spark(this.ctx, x, this.y, this.config));
  }

  updateColor() {
    this.r += (Math.random() - 0.5) * 10;
    this.r = Math.round(Math.min(80, Math.max(60, this.r)));
  }

  update() {
    this.updateColor();
    this.ctx.beginPath();
    this.ctx.rect(0, this.y, this.canvas.width, this.config.meanSparkSize);
    this.ctx.fillStyle = 'rgba(' + this.r + ', 0, 0, 1)';
    this.ctx.fill();

    for (let i = 0; i < this.sparks.length; i++) {
      if (this.sparks[i].update()) {
        this.sparks.splice(i, 1);
      }
    }
  }
}

class Spark {
  constructor(ctx, x, y, config) {
    this.ctx = ctx;
    this.pos = [x, y];
    this.size = config.meanSparkSize + (Math.random() - 0.5) * config.sparkSizeVariation;
    this.v = [
      config.meanSparkVelocity[0] * (Math.random() - 0.5),
      -1 * config.meanSparkVelocity[1] * Math.random()
    ];
    this.c = [
      Math.floor(Math.random() * 155) + 100,
      Math.floor(Math.random() * 80),
      0
    ];
    this.life = this.lifeOrig = Math.floor(config.meanSparkLife * Math.random());
    this.config = config;
  }

  move() {
    for (let i = 0; i < 2; i++) {
      this.pos[i] += this.v[i] * (1 - this.life / this.lifeOrig);
    }
  }

  getAlpha() {
    return Math.sqrt(this.life / this.lifeOrig) + (Math.random() - 0.5) / this.config.sparkBlink;
  }

  update() {
    this.move();
    if (!(this.life--)) return true;
    this.ctx.beginPath();
    this.ctx.rect(this.pos[0], this.pos[1], this.size, this.size);
    this.ctx.fillStyle =
      'rgba(' + this.c[0] + ', ' + this.c[1] + ', ' + this.c[2] + ', ' + this.getAlpha() + ')';
    this.ctx.fill();
  }
}

export default FireAnimation;
