import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Valyrian from '../assets/Valyrian.png';

const TopGamers = () => {
    const [gamers, setGamers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchGamers = async () => {
            try {
                const response = await axios.get('https://valyriansteelchallenge.onrender.com/top-gamers');
                setGamers(response.data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchGamers();
    }, []);

    if (loading) return <p className="text-center text-xl text-gray-300">Loading...</p>;
    if (error) return <p className="text-center text-xl text-red-500">Error: {error}</p>;

    return (
        <div className="relative bg-gray-900 min-h-screen">
            {/* Background image */}
            <img
                src={Valyrian} 
                alt="Valyrian Steel Background"
                className="absolute inset-0 h-full w-full object-cover opacity-50"
            />

            {/* Overlay content */}
            <div className="relative z-5 flex flex-col items-center justify-center min-h-screen">
                
            <h1 className="text-7xl font-bold text-red-700 mb-10 font-cinzeldecorative text-stroke">The Valyrian Steel</h1>
            <h1 className="text-7xl font-bold text-red-700 mb-10 font-cinzel text-stroke">Challenge</h1>
                
            <div className="bg-black bg-opacity-40 rounded-lg p-8 shadow-lg w-full max-w-7xl mx-auto">
                    <h2 className="text-white text-3xl font-semibold text-center mb-6 font-cinzel">Leaderboard</h2>

                    <table className="min-w-full table-auto border-collapse font-cinzel">
                        <thead>
                            <tr className="">
                                <th className="px-4 py-2 text-left text-gray-300 font-semibold">Position</th>
                                <th className="px-4 py-2 text-left text-gray-300 font-semibold">Team Name</th>
                                <th className="px-4 py-2 text-left text-gray-300 font-semibold">College Name</th>
                                <th className="px-4 py-2 text-left text-gray-300 font-semibold">Total Score</th>
                            </tr>
                        </thead>
                        <tbody>
                            {gamers.map((gamer, index) => (
                                <tr key={gamer._id} className={`${index % 2 === 0 ? 'bg-black bg-opacity-30' : 'bg-black bg-opacity-30'} `}>
                                    <td className="px-4 py-2 text-gray-200">{index + 1}</td>
                                    <td className="px-4 py-2 text-gray-200">{gamer.teamname}</td>
                                    <td className="px-4 py-2 text-gray-200">{gamer.collegename}</td>
                                    <td className="px-4 py-2 text-gray-200">{gamer.totalScore}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default TopGamers;
