import React from 'react';
import ReactCardFlip from 'react-card-flip';
import Valyrian from '../assets/Valyrian.png';

class FlipCard extends React.Component {
  constructor() {
    super();
    this.state = {
      isFlipped: false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    e.preventDefault();
    this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
  }

  render() {
    return (
      <ReactCardFlip isFlipped={this.state.isFlipped} flipDirection="vertical">
        {/* Front of the card */}
        <div className="front-card bg-black bg-opacity-40 p-4 rounded-lg shadow-lg flex flex-col justify-center items-center">
          <h3 className="text-2xl text-white text-center mb-4">This is the front of the card.</h3>
          <button
            className="bg-blue-500 text-white py-1.5 px-3 rounded text-sm"
            onClick={this.handleClick}
          >
            Click to flip
          </button>
        </div>
    
        {/* Back of the card */}
        <div className="back-card bg-black bg-opacity-40 p-4 rounded-lg shadow-lg flex flex-col justify-center items-center">
          <h3 className="text-2xl text-white text-center mb-4">This is the back of the card.</h3>
          {/* <button
            className="bg-red-500 text-white py-1.5 px-3 rounded text-sm"
            onClick={this.handleClick}
          >
            Click to flip back
          </button> */}
        </div>
      </ReactCardFlip>
    );
    
  }
}

function CardGrid() {
  return (
    <div
      className="relative min-h-screen bg-fixed bg-cover bg-center p-8"
      style={{ backgroundImage: `url(${Valyrian})` }}
    >
      <div className="bg-black bg-opacity-40 rounded-lg p-4 sm:p-6 md:p-8 shadow-lg w-full max-w-3xl sm:max-w-4xl md:max-w-5xl lg:max-w-6xl mx-auto">
      <h2 className="text-4xl font-bold text-white mb-8 text-center tracking-wider font-cinzeldecorative">
        Gamezz
      </h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 max-w-7xl mx-auto">
        {/* First Row: Three cards */}
        <FlipCard />
        <FlipCard />
        <FlipCard />

        {/* Second Row: Two cards */}
        <FlipCard />
        <FlipCard />
      </div>
      </div>
    </div>
  );
}

export default CardGrid;
