import React from 'react';

const FlipCard = ({ image, title, details, hue }) => {
  return (
    <div className="flip-card-container" style={{ '--hue': hue }}>
      <div className="flip-card relative w-full h-64 transform transition-transform duration-600 ease-out hover:rotate-y-180">
        <div className="card-front bg-dark absolute w-full h-full rounded-xl overflow-hidden flex justify-center items-center backface-hidden">
          <figure className="relative w-full h-full">
            <div className="img-bg absolute top-0 left-0 w-full h-full bg-dark bg-opacity-50 clip-path-front"></div>
            <img src={image} alt={title} className="w-full h-full rounded-xl" />
            <figcaption className="absolute top-0 right-3 p-2 bg-dark bg-opacity-50 text-white text-sm font-bold tracking-wide">
              {title}
            </figcaption>
          </figure>
          <ul className="mt-20 space-y-3 text-white text-center">
            {details.map((detail, index) => (
              <li key={index} className="border-b border-white border-opacity-20 pb-2">{detail}</li>
            ))}
          </ul>
        </div>

        <div className="card-back bg-dark absolute w-full h-full rounded-xl overflow-hidden flex justify-center items-center transform rotate-y-180 backface-hidden">
          <figure className="relative w-full h-full">
            <div className="img-bg absolute top-0 left-0 w-full h-full bg-dark bg-opacity-50 clip-path-back"></div>
            <img src={image} alt={title} className="w-full h-full rounded-xl" />
          </figure>
          <button className="mt-3 py-2 px-6 text-white border border-gray-400 rounded-full transition-colors duration-300 hover:bg-opacity-20 hover:bg-dark">
            Book
          </button>
        </div>
      </div>
    </div>
  );
};

const FlipCardList = () => {
  const cards = [
    {
      image: 'https://images.unsplash.com/photo-1486162928267-e6274cb3106f?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60',
      title: 'Brohm Lake',
      details: ['Detail 1', 'Detail 2', 'Detail 3', 'Detail 4', 'Detail 5'],
      hue: 220,
    },
    {
      image: 'https://images.unsplash.com/photo-1545436864-cd9bdd1ddebc?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60',
      title: 'Légende',
      details: ['Detail 1', 'Detail 2', 'Detail 3', 'Detail 4', 'Detail 5'],
      hue: 170,
    },
    // Add other card objects here...
  ];

  return (
    <div className="flex justify-between gap-5 flex-wrap p-5">
      {cards.map((card, index) => (
        <FlipCard
          key={index}
          image={card.image}
          title={card.title}
          details={card.details}
          hue={card.hue}
        />
      ))}
    </div>
  );
};

export default FlipCardList;
